import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";

import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

import Dropdown from "react-bootstrap/Dropdown";

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },

}));

const Recovered = () => {
  const classes = useStyles();
  const [allowances, setAllowances] = useState({ i: 0 });

  return (
    <Grid container spacing={2}>
      <Grid item xs={12} sm={6} lg={6}>
        <TextField
          autoComplete="fname"
          name="firstName"
          variant="outlined"
          required
          fullWidth
          id="firstName"
          label="Name"
          InputLabelProps={{
            classes: {
              root: classes.labelRoot,
            },
          }}
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          variant="outlined"
          required
          fullWidth
          id="email"
          label="Rs Bid price"
          name="email"
          autoComplete="email"
          InputLabelProps={{
            classes: {
              root: classes.labelRoot,
            },
          }}
        />
      </Grid>

      <Grid item xs={12}>
        <TextField
          variant="outlined"
          required
          fullWidth
          id="email"
          label="Email Address"
          name="email"
          autoComplete="email"
          InputLabelProps={{
            classes: {
              root: classes.labelRoot,
            },
          }}
        />
      </Grid>

      <Grid item xs={12} sm={6} lg={6}>
        <TextField
          autoComplete="name"
          name=""
          variant="outlined"
          required
          fullWidth
          id="firstName"
          label="PhoneNumber"
          InputLabelProps={{
            classes: {
              root: classes.labelRoot,
            },
          }}
        />
      </Grid>
      <Grid item xs={12} sm={6} lg={6}>
        <Button
          variant="contained"
          color="blue"
          onClick={() => {
            setAllowances({
              i: 1,
            });
          }}
        >
          Send OTP
        </Button>

        {allowances.i === 1 ? (
          <div>
            <Grid item xs={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="OTP"
                name="email"
                autoComplete="email"
                InputLabelProps={{
                  classes: {
                    root: classes.labelRoot,
                  },
                }}
              />
            </Grid>
            <Grid item xs={6}>
              <Button
                variant="contained"
                color="blue"

              >
                Verify
              </Button>
            </Grid>
          </div>
        ) : null}
      </Grid>

      <Grid item xs={12} sm={6} lg={6}>
        <Dropdown>
          <Dropdown.Toggle
            variant="success"
            id="dropdown-basic"
            style={{ backgroundColor: "blue" }}
          >
            cities
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">Tohana</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Gurgaon</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Delhi</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Grid>

      <Grid item xs={12} sm={6} lg={12}>
        <Dropdown>
          <Dropdown.Toggle
            variant="success"
            id="dropdown-basic"
            style={{ backgroundColor: "blue" }}
          >
            Blood Group
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">B+</Dropdown.Item>
            <Dropdown.Item href="#/action-2">A</Dropdown.Item>
            <Dropdown.Item href="#/action-3">A-</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </Grid>
      <Grid item xs={12}>
        <TextField
          variant="outlined"
          required
          fullWidth
          name="password"
          label="Password"
          type="password"
          id="password"
          autoComplete="current-password"
          InputLabelProps={{
            classes: {
              root: classes.labelRoot,
            },
          }}
        />
      </Grid>
    </Grid>
  );
};

export default Recovered;
