import React from "react";
import DistancSlider from "./DistanceSlider";
import CardGrid from "../cardDetails/CardsGrid";

class CardPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      distance: null,
    };
  }

  distancechange(v) {
    this.setState({
      distance: v,
    });
    console.log(this.state.distance);
  }

  render() {
    return (
      <div>
        <DistancSlider distancechange={this.distancechange} />

        <CardGrid distance={this.state.distance} />
      </div>
    );
  }
}

export default CardPage;
