import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Dropdown from "react-bootstrap/Dropdown";

import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    get marginTop() {
      return this._marginTop;
    },
    set marginTop(value) {
      this._marginTop = value;
    },
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    // marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    fontSize: 16,
  },
  labelRoot: {
    fontSize: 12,
  },
}));



const Recovered = () => {
    const classes = useStyles();
  const [allowances, setAllowances] = useState({ i: 0 });

    return (
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6} lg={6}>
          <TextField
            label="Name"
            defaultValue="Hello World"
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.labelRoot,
              },
            }}
          />
        </Grid>
        <Grid item xs={12} lg={6}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="email"
            label="Rs Bid price"
            name="email"
            autoComplete="email"
            InputLabelProps={{
              classes: {
                root: classes.labelRoot,
              },
            }}
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
          />
        </Grid>

        <Grid item xs={12} sm={6} lg={6}>
          <TextField
            autoComplete="name"
            name=""
            variant="outlined"
            required
            fullWidth
            id="firstName"
            label="PhoneNumber"
          />
        </Grid>
        <Grid item xs={12} sm={6} lg={6}>
          <Button
            variant="contained"
            color="blue"
            onClick={() => {
              setAllowances({
                i: 1,
              });
            }}
          >
            Send OTP
          </Button>
        </Grid>

        {allowances.i === 1 ? (
          <Grid container spacing={2}>
            <Grid item xs={6} lg={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="OTP"
                name="email"
                autoComplete="email"
              />
            </Grid>
            <Grid item xs={6} lg={6}>
              <Button variant="contained" color="blue">
                Verify
              </Button>
            </Grid>
          </Grid>
        ) : null}

        <Grid item xs={12} sm={6} lg={6}>
          <Dropdown>
            <Dropdown.Toggle
              variant="success"
              id="dropdown-basic"
              style={{ backgroundColor: "blue" }}
            >
              cities
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">Tohana</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Gurgaon</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Delhi</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </Grid>

        <Grid item xs={12} sm={6} lg={12}>
          <Dropdown>
            <Dropdown.Toggle
              variant="success"
              id="dropdown-basic"
              style={{ backgroundColor: "blue" }}
            >
              Blood Group
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">B+</Dropdown.Item>
              <Dropdown.Item href="#/action-2">A</Dropdown.Item>
              <Dropdown.Item href="#/action-3">A-</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </Grid>
        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />
        </Grid>
      </Grid>
    );
};

export default Recovered;
