import React from "react";
import DistancSlider from "../../filters/DistanceSlider";
import PriceSlider from "../../filters/PriceSlider";
import faker from "faker";
import Card from "../component/Cards";
import BloodGroup from "../../filters/BloodGroupDropDown";
import CitiesDropDown from "../../filters/CitiesDropdown";
import Grid from "@material-ui/core/Grid";

class CardPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      actual: 100,

    };
  }

  cardList2 = [
    {
      src: faker.image.avatar(),
      distance: 20,
      price: 4000,
    },
    {
      src: faker.image.avatar(),
      distance: 40,
      price: 3004,
    },
    {
      src: faker.image.avatar(),
      distance: 60,
      price: 0,
    },
    {
      src: faker.image.avatar(),
      distance: 80,
      price: 400,
    },
    {
      src: faker.image.avatar(),
      distance: 100,
      price: 2004,
    },
  ];

  checkvalue1 = (v) => {
    this.setState({
      actual: v,
    });
  };

  render() {
    return (
      <div>
        <div
          style={{
            padding: "50px",
            borderRadius: "25px",
            backgroundColor: "#F1F1F1",
            marginLeft: "10px",
            marginRight: "10px",
            marginTop: "20px",
          }}
        >
          <Grid container spacing={3}>
            <Grid item xs={5}>
              <DistancSlider checkvalue={this.checkvalue1} />
            </Grid>
            <Grid item xs={5}>
              <PriceSlider />
            </Grid>
            <Grid item xs={4}>
              <CitiesDropDown />
            </Grid>
            <Grid item xs={4}>
              <BloodGroup />
            </Grid>
          </Grid>
        </div>
        {/* {this.cardList.array.map((items, index) => {
          return this.state.actual == null
            ? this.cardList.array
            : this.state.actual >= this.cardList.array[index].props.distance
            ? this.cardList.array[index]
            : null;
        })} */}

        {this.cardList2.map((obj, key) => {
          return this.state.actual == 100 ? (
            <Card
              imgsrc={obj.src}
              distance={obj.distance}
              ratings={obj.ratings}
            />
          ) : this.state.actual >= obj.distance ? (
            <Card
              imgsrc={obj.src}
              distance={obj.distance}
              ratings={obj.ratings}
            />
          ) : null;
        })}
      </div>
    );
  }
}

export default CardPage;
