import React from "react";
import DistancSlider from "../../filters/DistanceSlider";
import PriceSlider from "../../filters/PriceSlider";
import faker from "faker";
import Card from "../component/Cards";
import BloodGroup from "../../filters/BloodGroupDropDown";
import CitiesDropDown from "../../filters/CitiesDropdown";
import Grid from "@material-ui/core/Grid";

class CardPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            actual: null,
            price: null,
            data: [

            ]
        };

    };

    componentDidMount() {

        console.log("rishabh")
        const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const url = "http://localhost:8080/covid/getAllCovidUsers";
        const response = fetch(url, {
                method: "GET",
                mode: "cors",
                cache: "no-cache",
                credentials: "same-origin",

            })
            .then((response) => response.json())
            .then((response) => this.responsevalue(response))
            .catch(() =>
                console.log("Can’t access " + url + " response. Blocked by browser")
            );


    };

    responsevalue = (response) => {

        let a = response.map((data) => {

            return {

                src: faker.image.avatar(),

                price: data.bidPrice

            }

        })

        this.setState({
            data: a
        })

    }

    checkvalue1 = (v) => {
        this.setState({
            actual: v,
        });
    };
    checkvalue2 = (v) => {
        this.setState({
            price: v,
        });
        console.log(v);
    };


    render() {
        return ( <
                div >
                <
                div style = {
                    {
                        padding: "50px",
                        borderRadius: "25px",
                        backgroundColor: "#F1F1F1",
                        marginLeft: "10px",
                        marginRight: "10px",
                        marginTop: "20px",
                    }
                } >
                <
                Grid container spacing = { 3 } >
                <
                Grid item xs = { 5 } >
                <
                DistancSlider checkvalue = { this.checkvalue1 }
                /> < /
                Grid > <
                Grid item xs = { 5 } >
                <
                PriceSlider checkvalue = { this.checkvalue2 }
                /> < /
                Grid >

                <
                Grid item xs = { 4 } >
                <
                BloodGroup / >
                <
                /Grid> < /
                Grid > <
                /div> {
                this.state.data.map((obj, key) => {
                        return this.state.actual == null && this.state.price == null ? ( <
                            Card imgsrc = { this.state.data[key].src }
                            distance = "20"
                            ratings = "4" /
                            >
                        ) : this.state.actual >= obj.distance && this.state.price == null ? ( <
                            Card imgsrc = { this.state.data[key].src }
                            distance = { obj.distance }
                            ratings = { obj.price }
                            />
                        ) : this.state.actual >= obj.distance && obj.price <= this.state.price ? ( <
                            Card imgsrc = { this.state.data[key].src }
                            distance = { obj.distance }
                            ratings = { obj.price }
                            />
                        ) : this.state.price >= obj.price && this.state.actual == null ? ( <
                            Card imgsrc = { this.state.data[key].src }
                            distance = { obj.distance }
                            ratings = { obj.price }
                            />  




                        ) : null
                    }


                )
            } <
            /div>
    );
}

}

export default CardPage;
