import React, { useState, Component } from "react";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Dropdown from "react-bootstrap/Dropdown";
import Link from "@material-ui/core/Link";

import { makeStyles } from "@material-ui/core/styles";

export default class Recovered extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      password: "",
      i: 0,
      phonenumber: "",
    };
  }
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "http://localhost:8080/covid/add/covid";
    const response = fetch(url, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(this.state),
    })
      .then((response) => response.text())
      .then((response) => console.log(response))
      .catch(() =>
        console.log("Canâ€™t access " + url + " response. Blocked by browser")
      );
  };

  render() {
    const paper = {
      marginTop: "64px",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
    };
    const form = {
      width: "100%",
      marginTop: "24px",
    };
    const submit = {
      margin: "24px 0px 16px",
    };
    const { name, email, password } = this.state;

    return (
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6} lg={6}>
          <TextField
            autoComplete="name"
            name="name"
            variant="outlined"
            required
            fullWidth
            id="name"
            label="Name"
            autoFocus
            value={name}
            onChange={this.changeHandler}
          />
        </Grid>
        <Grid item xs={12} lg={6}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="bidprice"
            label="Rs Bid price"
            name="bidprice"
            autoComplete="bidprice"
            value={name}
            value={name}
            onChange={this.changeHandler}
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            value={name}
            onChange={this.changeHandler}
          />
        </Grid>

        <Grid item xs={12} sm={6} lg={6}>
          <TextField
            autoComplete="name"
            name="phonenumber"
            variant="outlined"
            required
            fullWidth
            id="phonenumber"
            label="PhoneNumber"
            value={name}
            onChange={this.changeHandler}
          />
        </Grid>
        <Grid item xs={12} sm={6} lg={6}>
          <Button
            variant="contained"
            color="blue"
            onClick={() => {
              this.setState({
                i: 1,
              });
            }}
          >
            Send OTP
          </Button>
        </Grid>

        {this.state.i === 1 ? (
          <Grid container spacing={2} style={{ paddingLeft: "10px" }}>
            <Grid item xs={6} lg={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="OTP"
                name="email"
                autoComplete="email"
                value={name}
                onChange={this.changeHandler}
              />
            </Grid>
            <Grid item xs={6} lg={6}>
              <Button variant="contained" color="blue">
                Verify
              </Button>
            </Grid>
          </Grid>
        ) : null}

        <Grid item xs={12} sm={6} lg={6}>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            onChange={handleChange}
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </Grid>

        <Grid item xs={12} sm={6} lg={12}>
          <Dropdown>
            <Dropdown.Toggle
              variant="success"
              id="dropdown-basic"
              style={{ backgroundColor: "blue" }}
            >
              Blood Group
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">B+</Dropdown.Item>
              <Dropdown.Item href="#/action-2">A</Dropdown.Item>
              <Dropdown.Item href="#/action-3">A-</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </Grid>
        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            value={name}
            onChange={this.changeHandler}
          />
        </Grid>
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          //   disabled={!validateForm()}
          style={submit}
        >
          Sign Up
        </Button>
        <Grid container justify="flex-end">
          <Grid item>
            <Link href="Signin" variant="body2">
              Already have an account? Sign in
            </Link>
          </Grid>
        </Grid>
      </Grid>
    );
  }
}

