import React, { Component } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Dropdown from "react-bootstrap/Dropdown";

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    fontSize: 16,
  },
  labelRoot: {
    fontSize: 16,
  },
}));

export default class SignUp extends Component{
  classes = useStyles();
  render() {
    return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}></Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <div style={{ paddingTop: "20px" }}>
          <Grid container spacing={2}>
            <Grid item xs={6}>
              <Button variant="contained" color="white">
                Covid
              </Button>
            </Grid>
            <Grid item xs={6}>
              <Button variant="contained" color="white">
                Recovered
              </Button>
            </Grid>
          </Grid>
        </div>

        <form className={classes.form} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6} lg={6}>
              <TextField
                autoComplete="fname"
                name="firstName"
                variant="outlined"
                required
                fullWidth
                id="firstName"
                label="Name"
                InputLabelProps={{
                  classes: {
                    root: classes.labelRoot,
                  },
                }}
              />
            </Grid>
            <Grid item xs={12} lg={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="Rs Bid price"
                name="email"
                autoComplete="email"
                InputLabelProps={{
                  classes: {
                    root: classes.labelRoot,
                  },
                }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                InputLabelProps={{
                  classes: {
                    root: classes.labelRoot,
                  },
                }}
              />
            </Grid>

            <Grid item xs={12} sm={6} lg={6}>
              <TextField
                autoComplete="fname"
                name=""
                variant="outlined"
                required
                fullWidth
                id="firstName"
                label="PhoneNumber"
                InputLabelProps={{
                  classes: {
                    root: classes.labelRoot,
                  },
                }}
              />
            </Grid>
            {this.state.recovered && (
              <Grid item xs={12} sm={6} lg={6}>
                <Dropdown>
                  <Dropdown.Toggle
                    variant="success"
                    id="dropdown-basic"
                    style={{ backgroundColor: "blue" }}
                  >
                    cities
                  </Dropdown.Toggle>

                  <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Tohana</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Gurgaon</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Delhi</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Grid>
            )}

            <Grid item xs={12} sm={6} lg={12}>
              <Dropdown>
                <Dropdown.Toggle
                  variant="success"
                  id="dropdown-basic"
                  style={{ backgroundColor: "blue" }}
                >
                  Blood Group
                </Dropdown.Toggle>

                <Dropdown.Menu>
                  <Dropdown.Item href="#/action-1">B+</Dropdown.Item>
                  <Dropdown.Item href="#/action-2">A</Dropdown.Item>
                  <Dropdown.Item href="#/action-3">A-</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                InputLabelProps={{
                  classes: {
                    root: classes.labelRoot,
                  },
                }}
              />
            </Grid>
          </Grid>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="white"
            className={classes.submit}
          >
            Sign Up
          </Button>
          <Grid container justify="flex-end">
            <Grid item>
              <Link href="/login" variant="body2" style={{ fontSize: "14px" }}>
                Already have an account? Sign in
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={5}></Box>
    </Container>
  );
  }

}
