import React from "react";
import DistancSlider from "./DistanceSlider";
import PriceSlider from "./PriceSlider";
import RatingsSlider from "./RatingsSlider";
import faker from "faker";
import Card from "../cardDetails/Cards";
import BloodGroup from "./BloodGroupDropDown";
import CitiesDropDown from "./CitiesDropdown";
import Grid from "@material-ui/core/Grid";

class CardPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      actual: null,
    };
  }

CardList = {
    array: [
      <Card imgsrc={faker.image.avatar()} distance={20} ratings="4" link="" />,
      <Card imgsrc={faker.image.avatar()} distance={30} ratings="4" link="" />,
      <Card imgsrc={faker.image.avatar()} distance={40} ratings="4" link="" />,
      <Card imgsrc={faker.image.avatar()} distance={50} ratings="3" link="" />,
      <Card imgsrc={faker.image.avatar()} distance={60} ratings="4" link="" />,
      <Card imgsrc={faker.image.avatar()} distance={90} ratings="4" link="" />,
      <Card imgsrc={faker.image.avatar()} distance={100} ratings="4" link="" />,
    ],
  };
  // useStyles = makeStyles((theme) => ({
  //   root: {
  //     flexGrow: 1,
  //     marginLeft: "10px",
  //     marginRight: "10px",
  //     marginTop: "20px",
  //   },
  //   paper: {
  //     padding: theme.spacing(2),
  //     textAlign: "center",
  //     color: theme.palette.text.secondary,
  //   },
  // }));

  // componentDidUpdate() {
  //   this.setState({
  //     actual: null,
  //   });
  // }

  checkvalue1 = (v) => {
    this.setState({
      actual: v,
    });
  };
  // classes = this.useStyles();
  render() {
    return (
      <div>
        <div
          style={{
            padding: "50px",
            borderRadius: "25px",
            backgroundColor: "#F1F1F1",
            marginLeft: "10px",
            marginRight: "10px",
            marginTop: "20px",
          }}
        >
          <Grid container spacing={3}>
            <Grid item xs={5}>
              <DistancSlider checkvalue={this.checkvalue1} />
            </Grid>
            <Grid item xs={5}>
              <PriceSlider />
            </Grid>
            <Grid item xs={4}>
              <RatingsSlider />
            </Grid>
            <Grid item xs={4}>
              <CitiesDropDown />
            </Grid>
            <Grid item xs={4}>
              <BloodGroup />
            </Grid>
          </Grid>
        </div>
        {this.jain.array.map((items, index) => {
          return this.state.actual == null
            ? this.ardList.array
            : this.state.actual >= this.jain.array[index].props.distance
            ? this.jain.array[index]
            : null;
        })}
      </div>
    );
  }
}

export default CardPage;
