import React from "react";
import DistancSlider from "../../filters/DistanceSlider";
import PriceSlider from "../../filters/PriceSlider";
import faker from "faker";
import Card from "../component/Cards";
import BloodGroup from "../../filters/BloodGroupDropDown";
// import CitiesDropDown from "../../filters/CitiesDropdown";
import Grid from "@material-ui/core/Grid";

class CardPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        actual: null,
        price: null,
        data:[],
        latitude:null,
        longitude:null,
        distance:null,
        i:0

      }
    };

   componentDidMount() {
    window.navigator.geolocation.getCurrentPosition(
      (position) => {
        this.setState({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          i:1
        });
      }
    )

    const url = "http://localhost:8080/covid/getAllCovidUsers";
    fetch(url, {
      method: "GET",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
    })
      .then((response) => response.json())
      .then((response) => this.responsevalue(response))
      .catch(() =>
        console.log("Can’t access " + url + " response. Blocked by browser")
      );  
  };



  responsevalue=(response)=>{
     console.log(response)
    let a=response.map((data) => {
    return { src:faker.image.avatar(),price:data.bidPrice}})
    this.setState({
      data:a
    }
  )
}


location=()=>{

  const url1 = "http://localhost:8080/filters/distance";
  var q = {
  latitude:this.state.latitude,
  longitude:this.state.longitude,
  distance :2000

  }

  console.log(q);

  fetch(url1, {
  method: "POST",

  headers: {
    "Content-Type": "application/json",
  },

  body: JSON.stringify(q),
})
  .then((response) => response.json())
  .then((response) => console.log(response))
  .catch(() =>
    console.log(" rishabh " + url1 + " response. Blocked by browser")
  );
}

  checkvalue1 = (v) => {
    this.setState({
      distance:v,
    });
  };
  checkvalue2 = (v) => {
    this.setState({
      price: v,
    });
    console.log(v);
  };


  render() 
  {
    return (
      <div>
        {this.state.i===1?this.location():null}
        <div
          style={{
            padding: "50px",
            borderRadius: "25px",
            backgroundColor: "#F1F1F1",
            marginLeft: "10px",
            marginRight: "10px",
            marginTop: "20px",
          }}
        >
          <Grid container spacing={3}>
            <Grid item xs={5}>
              <DistancSlider checkvalue={this.checkvalue1} />
            </Grid>
            <Grid item xs={5}>
              <PriceSlider checkvalue={this.checkvalue2} />
            </Grid>

            <Grid item xs={4}>
              <BloodGroup />
            </Grid>
          </Grid>
        </div>
        {this.state.data.map((obj, key) => {
          return this.state.actual == null && this.state.price == null ? (
            <Card
              imgsrc={this.state.data[key].src}
              Price={this.state.data[key].price}
              ratings="4"
            />
          ) : this.state.actual >= obj.distance && this.state.price == null ? (
            <Card
              imgsrc={this.state.data[key].src}
              distance={obj.distance}
              ratings={obj.price}
            />
          ) : this.state.actual >= obj.distance && obj.price <= this.state.price ? (
            <Card
              imgsrc={this.state.data[key].src}
              distance={obj.distance}
              ratings={obj.price}
            />
          ) : this.state.price >= obj.price && this.state.actual == null ? (
            <Card
              imgsrc={this.state.data[key].src}
              distance={obj.distance}
              ratings={obj.price}
            />  
          ):null
        }


        )
        }
      </div>
    );
  }

}

export default CardPage;
